import React, { useContext, useEffect, useState } from 'react'
import { useNavigate, useParams } from 'react-router-dom'
import { DiaryStateContext } from '../App'
import Button from '../components/Button'
import Header from '../components/Header'
import { getStringDate } from '../util/date'

const Diary = () => {
  //Path Variable
  const { id } = useParams()
  console.log(id)

  const [diaryDetail, setDiaryDetail] = useState()

  const navigate = useNavigate()

  const diaryList = useContext(DiaryStateContext)

  useEffect(() => {
    if (diaryList.length >= 1) {
      const targetDiary = diaryList.find(
        //
        (it) => Number(it.id) === Number(id),
      )
      console.log(targetDiary)

      if (targetDiary) {
        setDiaryDetail(targetDiary)
      } else {
        alert('존재하지 않는 일기입니다. 메인 페이지로 돌아갑니다. ')
        navigate('/', { replace: true })
      }
    }
  }, [id, diaryList])

  const pagebackHandler = () => {
    navigate(-1)
  }

  const goEditHandler = () => {
    navigate('/edit/:id')
  }

  //useEffect()의 diaryDetail은 useEffect()에서 setDiary(targetDiary)를 통해
  //targetDiary를 추적 중.
  console.log('diaryDetail', diaryDetail)

  if (!diaryDetail) {
    return <div>다이어리가 없습니다.</div>
  } else {
    return (
      <div>
        <Header //
          headText={`${getStringDate(new Date(diaryDetail.date))}`}
          leftChild={
            <Button //
              text={'< 뒤로가기'}
              onClick={pagebackHandler}
            />
          }
          rightChild={
            <Button //
              text={'수정하기'}
              onClick={goEditHandler}
            />
          }
        />
      </div>
    )
  }
}

export default Diary
